mapper.select:
    select 

    from marks


	        mark_id = #{markId}


	        and deduct = #{deduct,jdbcType=INTEGER}


	        and INSTR(cause, #{cause,jdbcType=VARCHAR})


	        and stu_id = #{stuId,jdbcType=VARCHAR}


    order by deduct desc
    limit #{offset}, #{limit}

    select 
    count(*)
    from marks


	        mark_id = #{markId}


	        and deduct = #{deduct,jdbcType=INTEGER}


	        and INSTR(cause, #{cause,jdbcType=VARCHAR})


	        and stu_id = #{stuId,jdbcType=VARCHAR}
