mapper.select:
  select 

  from user


  		and userId = #{userId}


  		and name = #{name}


  		and phone = #{phone}


  		and realname = #{realname}


  		and school = #{school}


  		and major = #{major}


  		and studentid = #{studentid}


  ORDER BY create_time DESC
	LIMIT #{offset},#{limit}

select count(*)
from user


		and userId = #{userId}


		and name = #{name}


		and phone = #{phone}


		and realname = #{realname}


		and school = #{school}


		and major = #{major}


		and studentid = #{studentid}

select 

from user
where phone = #{phone} and password = #{password}
